package com.IF_Else;

import java.util.Iterator;

public class IncreasingValue {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int totalValue = 10;
		System.out.print("Value increasing from : ");
		for(int i=0; i<=totalValue; i++) {
			System.out.print(i+", ");
		}

	}
}
